using System;using System.Collections.Generic;class BrowserBuddy{    private TabHistory activeTab;    private Stack<TabHistory> closedTabs;    public BrowserBuddy()    {        activeTab = new TabHistory();        closedTabs = new Stack<TabHistory>();    }    public void VisitPage(string url)    {        activeTab.Visit(url);        Console.WriteLine("Visited: " + url);    }    public void Back()    {        activeTab.Back();    }    public void Forward()    {        activeTab.Forward();    }    public void CloseTab()    {        closedTabs.Push(activeTab);        activeTab = new TabHistory();        Console.WriteLine("Tab closed.");    }    public void RestoreTab()    {        if (closedTabs.Count > 0)        {            activeTab = closedTabs.Pop();            Console.WriteLine("Tab restored.");        }        else        {            Console.WriteLine("No closed tabs to restore.");        }    }    public void ShowCurrent()    {        Console.WriteLine("Current Page: " + activeTab.GetCurrentPage());        activeTab.DisplayHistory();    }}